package basicJava_Program;

public class Demo_TypesOfDatatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b=10;
		int i=20;
		short sh=30;
        long l=40;
        double d=50;
        float f=60.0125f;
        char c='A';
        boolean b1=true;
        String s="abc123&***   jklm";
        System.out.println("Vale of byte = "+b);
        System.out.println("Vale of short = "+sh);
        System.out.println("Vale of int = "+i);
        System.out.println("Vale of long = "+l);
        System.out.println("Vale of double = "+d);
        System.out.println("Vale of float = "+f);
        System.out.println("Vale of char = "+c);
        System.out.println("Vale of boolean = "+b1);
        System.out.println("Vale of string = "+s);

	}

}